import { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'  

function Movie() {
    const link = 'https://image.tmdb.org/t/p/original'
    const videolink = 'https://www.youtube.com/watch?v='
    const setVoteClass = (vote) => {
        if (vote >= 8) {
            return "green";
        } else if (vote >= 5) {
            return "orange";
        } else {
            return "red";
        }
    }

    const { id } = useParams()

    const [movie, setMovie] = useState(null)

    const [video, setVideo] = useState(null)

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie/ ' + id + '?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU')
            .then(res => res.json())
            .then(data => {
                setMovie(data)
            })
    }, [])

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie/' + id + '/videos?api_key=9122d3e99f5cf10f65b111a1dcd51b20')
            .then(res => res.json())
            .then(video => {
                setVideo(video)
            })
    }, [])

    console.log(video.key);

    if (!movie) return null

    if (!video) return null

    const genreList = movie.genres.map((g, i) => {
        return (
            <>
                <li className="movie-genre-list" key={i}>
                    <a href="" className="movie-genre-name">
                        {g.name}
                    </a>
                </li>
            </>
        )
    })

    return (
        <div className="movie">
            <div className="movie-top">
                <img className="movie-backdrop" src={link + movie.backdrop_path} />
                <video src={`https://www.youtube.com/watch?v=${videos.key}`}></video>
            </div>
            <div className="movie-bottom">
                <div className="movie-left">
                    <div className="movie-pictures">
                        <img className="movie-poster" src={link + movie.poster_path} />
                        <span className={`movie-vote-average ${setVoteClass(movie.vote_average)}`}>{movie.vote_average}</span>
                    </div>
                </div>
                <div className="movie-right">
                    <div className="movie-info">
                        <h1 className="movie-title">{movie.title}</h1>
                        <h1 className="movie-original-title">{movie.original_title}</h1>
                        <h3 className="movie-original-tagline">{movie.tagline}</h3>
                        <p className="movie-release-date">{movie.release_date}</p>
                        <p className="movie-budget">{`${movie.budget}$`}</p>
                        <p className="movie-genre">{movie.genres && genreList}</p>
                        <p className="movie-overview">{movie.overview}</p>
                        <p className="movie-original-lang">{`Оригинальный язык: ${movie.original_language}`}</p>
                        <p className="movie-popularity">{`Популярность: ${movie.popularity}`}</p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Movie;
