import { Link, useParams } from 'react-router-dom'
import { BrowserRouter, Route, Router, Switch } from 'react-router-dom'

function Person() {
    const link = 'https://image.tmdb.org/t/p/w500'

    const { id } = useParams()

    const [person, setPerson] = useState(null)

    return (
        <div className="person-card">
            <div className="person-card-top">
                <img className="person-card-img" src={link + props.data.poster_path} alt="" />
                <p className="person-card-overview">{`Описание: ${props.data.overview}`}</p>
            </div>
            <div className="person-card-bottom">
                <h1 className="person-card-title">{person.name}</h1>
                <p className="person-card-release_date">{props.data.release_date}</p>
            </div>
        </div>
    )
}

export default Person;
