import InfiniteCinemaRow from './home-page-comp/InfiniteCinemaRow'

function Welcome() {

    let animateItems = document.querySelectorAll('.animate-items')

    if (animateItems.length > 0) {
        window.addEventListener('scroll', animOnScroll)
        function animOnScroll(params) {
            for (let index = 0; index < animateItems.length; index++) {
                const animateItem = animateItems[index];
                const animateItemHeight = animateItem.offsetHeight;
                const animateItemOffset = offset(animateItem).top;
                animateStart = 4;

                let animateItemPoint = window.innerHeight - animateItemHeight / animateStart;
                if (animateItemHeight > window.innerHeight) {
                    animateItemPoint = window.innerHeight - window.innerHeight / animateStart;
                }

                if ((pageYOffset > animateItemOffset - animateItemPoint) && pageYOffset < (animateItemOffset + animateItemHeight)) {
                    animateItem.classList.add('active')
                } else {
                    animateItem.classList.remove('active')
                }
            }
        }
        function offset(el) {
            const rect = el.getBoundingClientRect(),
                scrollLeft = window.pageXOffset || document.documentElement.scrollLeft,
                scrollUp = window.pageYOffset || document.documentElement.scrollUp;
            return { top: rect.top + scrollUp, left: rect.left + scrollLeft }
        }
    }

    return (
        <>
            <section className="welcome">
                <div className="container">
                    <div className="welcome-inner">
                        <div className="welcome-left">
                            <div className="welcome-title title">
                                <h1>Ansagang Movies</h1>
                            </div>
                            <div className="welcome-info">
                                <p>На ansagang вы можете не только найти информацию об интересующих вас фильмах, но и посмотреть их бесплатно онлайн в течение 7 дней или всего за 1 рубль. Мы предлагаем нашим пользователям смотреть фильмы и телесериалы легально и в хорошем качестве, выбрав их из нашей обширной базы данных, насчитывающей тысячи наименований. </p>
                            </div>
                        </div>
                        <div className="welcome-right">
                            <div className="welcome-imgs">
                                <img className="welcome-img" src={require('/Users/Ансар/Documents/react/ansa-app/src/img/target.svg').default} alt="" />
                                <img className="welcome-img-back" src={require('/Users/Ансар/Documents/react/ansa-app/src/img/target-white.svg').default} alt="" />
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            <InfiniteCinemaRow />
        </>
    )
}

export default Welcome;
