import { useEffect, useState} from 'react'
import {Link, useParams } from 'react-router-dom'

function Movie() {
    const { id } = useParams()

    const [movie, setMovie] = useState(null)

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie/615457?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU')
            .then(res => res.json())
            .then(data => {
                setMovie(data)
            })
    }, [])

    console.log(movie);

    return(
        <div className="movie">
            <h1>{movie.title}</h1>
        </div>
    )
}

export default Movie;
