import { useState, useEffect } from 'react'
import MoviePoster from './MoviePoster';
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'

function MoviePosters() {

    const [postermovies, setPosterMovies] = useState([])

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie?sort_by=popularity?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&page=1')
            .then(res => res.json())
            .then(data => {
                setPosterMovies(data.results)
            })
    }, [])

    let settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        // centerMode: true
    }

    return (
        <>
            <Slider className="movie-posters" {...settings}>
                {
                    postermovies.map(item => (
                        <MoviePoster data={item} />
                    ))
                }
            </Slider>
        </>
    )
}

export default MoviePosters;
