import { useState, useEffect } from 'react'
import MovieCard from './MovieCard';
import { useParams } from 'react-router-dom'

function Search() {
    const searchBar = document.querySelector('.header-search-bar')
    const form = document.querySelector('.form')
    const searchValue = searchBar.nodeValue
    const { q } = useParams()

    const [movies, setMovies] = useState([])

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/search/movie?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&query=' + q + '&page=1')
            .then(res => res.json())
            .then(data => {
                setMovies(data.results)
            })
    }, [])

    // form.addEventListener("submit", (e) => {
    //     e.preventDefault();

    //     const q = searchBar.value;

    //     fetch('https://api.themoviedb.org/3/search/movie?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&query='+ q +'&page=1')
    //     .then(res => res.json())
    //     .then(data => {
    //         setMovies(data.results)
    //     })
    //   });

    return (
        <>
            <div className="movie-title title">
                <h1>Popular Movies</h1>
            </div>
            <div className="movie-list">
                {
                    movies.map(item => (
                        <MovieCard data={item} />
                    ))
                }
            </div>
        </>
    )
}

export default Search;
