import { useState, useEffect } from 'react'
import MovieCard from './MovieCard';
import { useParams } from 'react-router-dom'

function Search() {
    const search = document.querySelector('.header-search-bar-input')
    const form = document.querySelector('form')

    const [movies, setMovies] = useState([])

    setMovies(() => {
        fetch('https://api.themoviedb.org/3/search/movie?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&query=' + q + '&page=1')
            .then(res => res.json())
            .then(data => {
                setMovies(data.results)
            })
    }, [])

    form.addEventListener("submit", (e) => {
        e.preventDefault();

        const q = search.value;

        if (q) {
            setMovies(q);

            search.value = "";
        }   
    })

    return (
        <>
            <div className="movie-title title">
                <h1></h1>
            </div>
            <div className="movie-list">
                {
                    movies.map(item => (
                        <MovieCard data={item} />
                    ))
                }
            </div>
        </>
    )
}

export default Search;
