import {useState} from 'react'
import MovieItem from './MovieItem';

const movieOne = {
    title:'Spider-man : Far From Home',
    overview:' Peter Parkers relaxing European vacation takes an unexpected turn when Nick Fury shows up in his hotel room to recruit him for a mission. The world is in danger as four massive elemental creatures each representing Earth, air, water and fire emerge from a hole torn in the universe. Parker soon finds himself donning the Spider-Man suit to help Fury and fellow superhero Mysterio stop the evil entities from wreaking havoc across the continent.',
    img:'https://www.joblo.com/assets/images/joblo/news/2019/12/FFH.jpg'
}
let movieTwo = {
    title:"Soul",
    overview:'Joe is a middle-school band teacher whose life hasnt quite gone the way he expected. His true passion is jazz and hes good. But when he travels to another realm to help someone find their passion, he soon discovers what it means to have soul.',
    img:'https://gugimages.s3.us-east-2.amazonaws.com/wp-content/uploads/2021/01/03114954/tig65f19aav51.jpg'
}

function MovieList() {
    const [movies, setMovies] = useState(movieOne)

    function changeMovie() {
        if(true){
            setMovies(movieTwo)
        }
        setMovies(movieTwo)
    }

    return(
        <div className="movie-list">
            <button onClick={changeMovie}>
                <h1>Поменять</h1>
            </button>
            <MovieItem data={movies} />
            <MovieItem data={movies} />
            <MovieItem data={movies} />
            <MovieItem data={movies} />
            <MovieItem data={movies} />
            <MovieItem data={movies} />
        </div>
    )
}

export default MovieList;
