import { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import Slider from 'react-slick'
import CastCard from './comp/CastCard'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import ActorCard from './comp/ActorCard'
import MovieCard from './comp/MovieCard'
import SimilarMovieCard from './comp/SimilarMovieCard'
import { BrowserRouter, Route, Router, Switch } from 'react-router-dom'

function Movie() {
    const link = 'https://image.tmdb.org/t/p/original'
    const videolink = 'https://www.youtube.com/embed/'
    const setVoteClass = (vote) => {
        if (vote >= 8) {
            return "green";
        } else if (vote >= 5) {
            return "orange";
        } else {
            return "red";
        }
    }

    const { id } = useParams()

    const [movie, setMovie] = useState(null)

    const [videos, setVideo] = useState(null)

    const [cast, setCast] = useState(null)

    const [crew, setCrew] = useState(null)

    const [similarmovies, setSimilarMovies] = useState(null)

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie/ ' + id + '?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU')
            .then(res => res.json())
            .then(data => {
                setMovie(data)
            })
    }, [id])

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie/' + id + '/videos?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&page=1')
            .then(res => res.json())
            .then(videos => {
                setVideo(videos)
            })
    }, [id])

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie/' + id + '/credits?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&page=1')
            .then(res => res.json())
            .then(cast => {
                setCast(cast)
            })
    }, [id])

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie/' + id + '/credits?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&page=1')
            .then(res => res.json())
            .then(crew => {
                setCrew(crew)
            })
    }, [id])

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie/' + id + '/similar?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&page=1')
            .then(res => res.json())
            .then(similar => {
                setSimilarMovies(similar.results)
            })
    }, [id])

    if (!movie) return null

    if (!videos) return null

    if (!cast) return null

    if (!crew) return null

    if (!similarmovies) return null

    let settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1,
    }

    const genreList = movie.genres.map((g, i) => {
        return (
            <>
                <li className="movie-genre-list" key={i}>
                    <a href="" className="movie-genre-name">
                        {g.name}
                    </a>
                </li>
            </>
        )
    })

    return (
        <div className="movie">
            <div className="movie-trailer">
                <img className="movie-backdrop" src={link + movie.backdrop_path} />
                {
                    movie.backdrop_path ? (<img className="movie-backdrop" src={link + movie.backdrop_path}/>) : (<img className="movie-backdrop" src={require('./img/cool-background.png').default} alt="" />)
                }
                {
                    videos.results.length > 0 ? (<iframe frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen src={videolink + videos.results[0].key}></iframe>) : 'no'
                }
            </div>
            <div className="movie-info">
                <div className="movie-left">
                    <div className="movie-pictures">
                        <img className="movie-poster" src={link + movie.poster_path} />
                        <span className={`movie-vote-average ${setVoteClass(movie.vote_average)}`}>{movie.vote_average}</span>
                    </div>
                </div>
                <div className="movie-right">
                    <div className="movie-details">
                        <h1 className="movie-title">{movie.title}</h1>
                        <h1 className="movie-original-title">{movie.original_title}</h1>
                        <h3 className="movie-original-tagline">{movie.tagline}</h3>
                        <p className="movie-release-date">{movie.release_date}</p>
                        <p className="movie-budget">{`${movie.budget}$`}</p>
                        <p className="movie-genre">{movie.genres && genreList}</p>
                        <p className="movie-overview">{movie.overview}</p>
                        <p className="movie-overview">{movie.runtime / 60}</p>
                        <p className="movie-original-lang">{`Оригинальный язык: ${movie.original_language}`}</p>
                        <p className="movie-popularity">{`Популярность: ${movie.popularity}`}</p>
                    </div>
                </div>
            </div>
            <div className="movie-cast">
                <div className="cast-title title">
                    <h1>The Cast</h1>
                </div>
                <Slider className="actors-list" {...settings}>
                    {
                        cast.cast.map(item => (
                            <CastCard cast={item} />
                        ))
                    }
                </Slider>
            </div>
            <div className="movie-crew">
                <div className="crew-title title">
                    <h1>The Crew</h1>
                </div>
                <Slider className="actors-list" {...settings}>
                    {
                        cast.crew.map(item => (
                            <CastCard cast={item} />
                        ))
                    }
                </Slider>
            </div>
            <div className="similar-movies">
                <div className="similar-movie-title title">
                    <h1>Similar Movies</h1>
                </div>
                <Slider className="movies-list" {...settings}>
                    {
                        similarmovies.map(item => (
                            <SimilarMovieCard similar={item} />
                        ))
                    }
                </Slider>
            </div>
        </div>
    )
}

export default Movie;
