import { useState, useEffect } from 'react'
import MovieCard from './MovieCard'

function GenreList() {

    const [genremovies, setMoviesByGenre] = useState([])

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/discover/movie?api_key=9122d3e99f5cf10f65b111a1dcd51b20&with_genres='+ selectedMovie + '&language=ru-RU&page=1')
            .then(res => res.json())
            .then(data => {
                setMoviesByGenre(data.results)
            })
    }, [])
    const selectedMovie = e.target.value;

    return (
        <>
            <div className="genres-title title">
                <h1>Search By Genre</h1>
            </div>
            <div className="genre-selector">
                <select id="genres" onChange={(e) => {
                    setMoviesByGenre()
                }}>
                    <option value="genre">Select Genre</option>
                    <option value="28">Action</option>
                    <option value="animation">Animation</option>
                    <option value="documentary">Documentary</option>
                    <option value="drama">Drama</option>
                    <option value="family">Family</option>
                    <option value="fantasy">Fantasy</option>
                    <option value="history">History</option>
                    <option value="comedy">Comedy</option>
                    <option value="war">War</option>
                    <option value="crime">Crime</option>
                    <option value="mustery">Mustery</option>
                    <option value="sci-fi">Sci-fi</option>
                    <option value="horror">Horror</option>
                    <option value="adventure">Adventure</option>
                    <option value="western">Western</option>
                    <option value="thriller">Thriller</option>
                    <option value="TV-movie">TV movie</option>
                </select>
                {genremovies}
            </div>
            {/* <div className="movie-list">
                {
                    genremovies.map(item => (
                        <MovieCard data={item} />
                    ))
                }
            </div> */}
        </>
    )
}

export default GenreList;
