import { useState, useEffect } from 'react'
import MovieCard from './MovieCard';
import { useParams } from 'react-router-dom'
import Nav from './Nav'
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'

function SearchMovie() {

    const { q } = useParams()

    const [searchmovies, setSearchMovies] = useState([])
    const [searchTerm, setSearchTerm] = useState([])

    const handleOnChange = (e) => {
        setSearchTerm(e.target.value)
    }

    const handleOnSubmit = (e) => {
        e.preventDefault();

        fetch('https://api.themoviedb.org/3/search/movie?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&query=' + searchTerm + '&page=1')
            .then(res => res.json())
            .then(data => {
                setSearchMovies(data.results)
            })
    }

    let settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1,
        // centerMode: true
        
    }

    return (
        <>

            <div className="header-search-bar">
                <form onSubmit={handleOnSubmit} className="header-search-bar-form" action="">
                    <input onChange={handleOnChange} placeholder="Search a Movie" value={searchTerm} className="header-search-bar-input" type="search" />
                </form>
            </div>
            <div className="movie-title title">
                <h1></h1>
            </div>
            <Slider className="movie-list" {...settings}>
                {
                    searchmovies.map(item => (
                        <MovieCard data={item} />
                    ))
                }
            </Slider>
        </>
    )
}

export default SearchMovie;
