function MovieCard(props) {
    const link = 'https://image.tmdb.org/t/p/w500'
    const setVoteClass = (vote) => {
        if (vote >= 8) {
            return "green";
          } else if (vote >= 5) {
            return "orange";
          } else {
            return "red";
          }
    }

    return (
    <div className="movie-card">
        <div className="movie-card-top">
            <img className="movie-card-img" src={link + props.data.poster_path} alt="" />
            <p className="movie-card-overview">{props.data.overview}</p>
        </div>
        <div className="movie-card-bottom">
            <h1 className="movie-card-title">{props.data.title}</h1>
            <p className="movie-card-release_date">{props.data.release_date}</p>
            <span className={`movie-card-vote_average ${setVoteClass(vote_average)}`}>{props.data.vote_average}</span>
        </div>
    </div>
    )
}

export default MovieCard;
