import { useState, useEffect } from 'react'
import MovieCard from './MovieCard'

function GenreList() {

    return (
        <>
            <div className="genres-title title">
                <h1>Search By Genre</h1>
            </div>
            <div className="genre-selector">
                <select id="genres" onChange={(e) => {
                    const selectedMovie = e.target.value;
                    const [genremovies, setMoviesByGenre] = useState([])
                    useEffect(() => {
                        fetch('https://api.themoviedb.org/3/discover/movie?api_key=9122d3e99f5cf10f65b111a1dcd51b20&with_genres='+ selectedMovie + '&language=ru-RU&page=1')
                            .then(res => res.json())
                            .then(data => {
                                setMoviesByGenre(data.results)
                            })
                    }, [])
                }}>
                    <option className="movie-option" value="">Select Genre</option>
                    <option className="movie-option" value="28">Action</option>
                    <option className="movie-option" value="35">Animation</option>
                    <option className="movie-option" value="35">Documentary</option>
                    <option className="movie-option" value="31">Drama</option>
                    <option className="movie-option" value="35">Family</option>
                    <option className="movie-option" value="15">Fantasy</option>
                    <option className="movie-option" value="9">History</option>
                    <option className="movie-option" value="3">Comedy</option>
                    <option className="movie-option" value="3">War</option>
                    <option className="movie-option" value="4">Crime</option>
                    <option className="movie-option" value="2">Mustery</option>
                    <option className="movie-option" value="11">Sci-fi</option>
                    <option className="movie-option" value="1">Horror</option>
                    <option className="movie-option" value="6">Adventure</option>
                    <option className="movie-option" value="8">Western</option>
                    <option className="movie-option" value="67">Thriller</option>
                    <option className="movie-option" value="9">TV movie</option>
                </select>
            </div>
            <div className="movie-list">
                {
                    genremovies.map(item => (
                        <MovieCard data={item} />
                    ))
                }
            </div>
        </>
    )
}

export default GenreList;
