import { useState, useEffect } from 'react'
import MovieCard from './MovieCard';
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'

function PopMovieList() {

    const [popmovies, setPopMovies] = useState([])

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/movie/popular?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&page=1')
            .then(res => res.json())
            .then(data => {
                setPopMovies(data.results)
            })
    }, [])

    let settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1,
    }

    return (
        <>
            <div className="movie-title title">
                <h1>Popular Movies</h1>
            </div>
            <Slider className="movie-list" {...settings}>
                {
                    popmovies.map(item => (
                        <MovieCard data={item} />
                    ))
                }
            </Slider>
        </>
    )
}

export default PopMovieList;
