import { useState } from 'react'
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'

import MovieCard from './MovieCard';

function SearchMovie() {
    const [searchmovies, setSearchMovies] = useState([])
    const [searchTerm, setSearchTerm] = useState([])

    const handleOnChange = (e) => {
        setSearchTerm(e.target.value)
    }

    const handleOnSubmit = (e) => {
        e.preventDefault();
        if(searchTerm){
        fetch('https://api.themoviedb.org/3/search/movie?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&query=' + searchTerm + '&page=1')
            .then(res => res.json())
            .then(data => {
                setSearchMovies(data.results)
            })
        }
    }

    let properties = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 4,
        responsive:
        [
            {
                breakpoint:580,
                settings:{
                    slidesToShow: 3,
                    slidesToScroll: 3
                }
            },
            {
                breakpoint:280,
                settings:{
                    slidesToShow: 2,
                    slidesToScroll: 2
                }
            }
        ]
    }

    return (
        <>

            <div className="search-bar">
                <form onSubmit={handleOnSubmit} className="search-bar-form" action="">
                    <input onChange={handleOnChange} placeholder="Search a Movie" value={searchTerm} className="search-bar-input" type="search" />
                </form>
            </div>
            <Slider className="search-movies-list list" {...properties}>
                {
                    searchmovies.map(item => (
                        <MovieCard data={item} />
                    ))
                }
            </Slider>
        </>
    )
}

export default SearchMovie;
