import { Link } from 'react-router-dom'
import { useState } from 'react'
import Search from './Search'
import { useEffect } from 'react'
import MovieCard from './MovieCard';
import { useParams } from 'react-router-dom'

function Nav() {
    const [sidebar, setSideBar] = useState(false)
    const showSideBar = () => setSideBar(!sidebar)
    const [searchmovies, setSearchMovies] = useState([])
    const [searchTerm, setSearchTerm] = useState([])

    const { q } = useParams()

    useEffect(() => {
        fetch('https://api.themoviedb.org/3/search/movie?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&query=' + q + '&page=1')
            .then(res => res.json())
            .then(data => {
                setSearchMovies(data.results)
            })
    }, [])

    const handleOnChange = (e) => {
        setSearchTerm(e.target.value)
    }

    const handleOnSubmit = (e) => {
        e.preventDefault();

        fetch('https://api.themoviedb.org/3/search/movie?api_key=9122d3e99f5cf10f65b111a1dcd51b20&language=ru-RU&query=' + searchTerm + '&page=1')
            .then(res => res.json())
            .then(data => {
                setSearchMovies(data.results)
            })
    }

    return (
        <>
            <header>
                <div className="container">
                    <div className="header-inner">
                        <div className="header-logo" onClick={showSideBar}>
                            <img src={require('../img/target.svg').default} alt="" className="header-logo-img" />
                        </div>
                        <div className="header-search-bar">
                            <form onSubmit={handleOnSubmit} className="header-search-bar-form" action="">
                                <input onChange={handleOnChange} value={searchTerm} className="header-search-bar-input" type="search" />
                            </form>
                        </div>
                    </div>
                </div>
                <svg class="welcome-wave" fill="white" cdata-name="Layer 1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 120" preserveAspectRatio="none">
                    <path d="M0,0V46.29c47.79,22.2,103.59,32.17,158,28,70.36-5.37,136.33-33.31,206.8-37.5C438.64,32.43,512.34,53.67,583,72.05c69.27,18,138.3,24.88,209.4,13.08,36.15-6,69.85-17.84,104.45-29.34C989.49,25,1113-14.29,1200,52.47V0Z" opacity=".25" class="shape-fill"></path>
                    <path d="M0,0V15.81C13,36.92,27.64,56.86,47.69,72.05,99.41,111.27,165,111,224.58,91.58c31.15-10.15,60.09-26.07,89.67-39.8,40.92-19,84.73-46,130.83-49.67,36.26-2.85,70.9,9.42,98.6,31.56,31.77,25.39,62.32,62,103.63,73,40.44,10.79,81.35-6.69,119.13-24.28s75.16-39,116.92-43.05c59.73-5.85,113.28,22.88,168.9,38.84,30.2,8.66,59,6.17,87.09-7.5,22.43-10.89,48-26.93,60.65-49.24V0Z" opacity=".5" class="shape-fill"></path>
                    <path d="M0,0V5.63C149.93,59,314.09,71.32,475.83,42.57c43-7.64,84.23-20.12,127.61-26.46,59-8.63,112.48,12.24,165.56,35.4C827.93,77.22,886,95.24,951.2,90c86.53-7,172.46-45.71,248.8-84.81V0Z" class="shape-fill"></path>
                </svg>
            </header>
            <aside className={sidebar ? 'side-bar active' : 'side-bar'}>
                <div className="side-bar-inner">
                    <div className="side-bar-logo">
                        <img className="side-bar-logo-img" src={require('../img/target.svg').default} alt="" />
                        <h1 className="side-bar-logo-title">ansagang</h1>
                    </div>
                    <div className="side-bar-links">
                        <Link className="side-bar-link" to="/welcome">Main</Link>
                        <Link className="side-bar-link" to="/movies">Movies</Link>
                        <Link className="side-bar-link" to="/serials">Serials</Link>
                    </div>
                </div>
            </aside>
            <div className="movie-title title">
                <h1></h1>
            </div>
            <div className="movie-list">
                {
                    searchmovies.map(item => (
                        <MovieCard data={item} />
                    ))
                }
            </div>
        </>
    )
}

export default Nav;
